<template>
  <div class="list">
    <home-keywords v-if="!npmList.length && !npmList.error" />
    <van-empty
      v-if="!!npmList.error"
      class="custom-image"
      :image="emptyImg"
      description="暂无数据"
    />
    <div
      v-for="({
        name,
        version,
        description,
        owner,
        publisher,
        types,
        license,
        modified,
        keywords
      },
      index) in npmList"
      v-else
      :key="index"
      :class="{ 'list-item': true, selected: index === 0 }"
    >
      <!--
            <div class="list-item-icon">
            <img src="" alt=""></div>
            -->
      <div class="list-item-content">
        <div class="list-item-title" @click="goPackageDetail(name)">
          <span class="pkg-name">{{ name }}</span>
          <span class="hint">{{ version }}</span>
          <span v-if="owner">
            <span v-if="license" class="license" v-html="license"></span>
            <img
              v-if="types.ts === 'included'"
              class="ts-icon"
              alt="TypeScript support: included"
              title="TypeScript support: included"
              src="data:image/svg+xml;base64,"
            />
          </span>
        </div>
        <div class="list-item-description" v-html="description"></div>
        <div v-if="owner" class="list-item-bottom">
          <a class="owner">
            <img
              width="30"
              height="30"
              alt="owner"
              :src="
                `https://res.cloudinary.com/hilnmyskv/image/fetch/w_40,h_40,f_auto,q_80,fl_lossy/${owner.avatar}`
              "
            />
            {{ owner.name }}
          </a>
          <span class="hint">{{ modified | timeAgo }}</span>
        </div>
        <div v-else-if="publisher" class="list-item-bottom">
          <a v-if="publisher.avatars" class="owner">
            <img
              width="30"
              height="30"
              alt="owner"
              :src="`https://www.npmjs.com/${publisher.avatars.small}`"
            />
            {{ publisher.name }}
          </a>
          <span class="hint">{{ modified | timeAgo }}</span>
        </div>
        <div v-else class="list-item-bottom">
          <a class="owner">
            <svg viewBox="0 0 27.23 27.23" width="30" height="30">
              <rect fill="#333333" width="27.23" height="27.23" rx="2"></rect>
              <polygon
                fill="#fff"
                points="5.8 21.75 13.66 21.75 13.67 9.98 17.59 9.98 17.58 21.76 21.51 21.76 21.52 6.06 5.82 6.04 5.8 21.75"
              ></polygon>
            </svg>
            npm.io
          </a>
          <span class="hint">{{ modified | timeAgo }}</span>
        </div>

        <div v-if="keywords && keywords.length" class="tags_list">
          <span
            v-for="(keyword, i) in keywords"
            :key="i"
            class="tag_link"
            @click="getKeywordInf(keyword)"
          >
            {{ keyword }}
          </span>
        </div>
      </div>
    </div>
  </div>
</template>

<script lang="ts">
// https://github.com/vst93/myDictionary-uToolsPlugin/blob/master/assets/index.js
import { Vue, Component } from 'vue-property-decorator';
import homeKeywords from './home-keywords.vue';

import { getSuggestionList, getKeyWordList } from '../../utils/API';
import { _debounce } from '../../utils/util';
import noimage from '@/assets/custom-empty-image.png';

/* eslint-disable no-undef */
@Component({
  name: 'List',
  components: {
    'home-keywords': homeKeywords
  }
})
export default class List extends Vue {
  private emptyImg = noimage;
  private npmList: {}[] = [];

  goPackageDetail(name: string) {
    this.$router.push({
      name: 'Detail',
      params: { name }
    });
  }

  async fetchList(args: string) {
    this.npmList = args.includes('keywords:')
      ? await getKeyWordList(args)
      : ((await getSuggestionList(args)) as any[]);
    utools.setExpendHeight(660);
  }

  getKeywordInf(keyName: string) {
    utools.setSubInputValue('keywords:' + keyName);
  }

  private utoolSetInput(needSearch?: boolean) {
    this.$route.name === 'List' || needSearch
      ? utools.setSubInput(
          ({ text }: { text: string }) => {
            const txt = text.trim();
            if (!txt) {
              return;
            }
            _debounce(this.fetchList)(text);
          },
          '输入 keywords:angular 搜索相关库',
          true
        )
      : // 监听用户输入
        utools.setSubInput(({ text }: { text: string }) => {
          utools.findInPage(text);
          this.$store.dispatch('changeText', { searchText: text });
          // highlightManual('#manualBody', text);
        }, '搜索全文，选中文本回车键查询，T翻译；Tab切换界面');
  }

  created() {
    //@ts-ignore
    // 每当插件从后台进入到前台时，uTools 将会主动调用这个方法。
    utools.onPluginEnter(params => {
      console.log(params);
      const { type, payload } = params;
      switch (type) {
        case 'files':
          {
            const { devDependencies, dependencies } = window.requireMoudle(
              payload[0].path
            );
            console.log('--package文件匹配--:', dependencies, devDependencies);
            // https://api.npms.io/v2/package/mget
          }
          break;
        case 'text':
          this.utoolSetInput();
          break;
        case 'over':
          this.utoolSetInput(true);
          utools.setSubInputValue(payload);
          break;
        default:
          break;
      }
    });
  }

  async mounted() {
    const { keyword } = this.$route.params;
    if (keyword) {
      this.$nextTick(() => {
        utools.setSubInputValue(keyword);
      });
    }
  }
}
</script>

